/** *  */package au.edu.uq.smartass.maths;/** * @author carnivore * */public class DeltaAntiderivative extends MathsOp {	MathsOp hi, lo, expr;	private String[] texString={"\\biggl[","\\biggr]^{","}_{", "}"}; 	/**	 * @param hi	high limit	 * @param lo 	low limit	 * @param expr	expression under integral sign	 */	public DeltaAntiderivative(MathsOp lo, MathsOp hi, MathsOp expr) {		this.hi = hi;		this.lo = lo;		this.expr = expr;		if (getTex()==null)			setTex(texString);		}	public String toString() {		String[] currentTex=getLocalTex();		return currentTex[0] + expr + currentTex[1] + hi + currentTex[2] + lo + currentTex[3];	}}
